import React, { useState } from 'react';
import Header from './components/Header';
import PersonalInfo from './components/PersonalInfo';
import Experience from './components/Experience';
import Education from './components/Education';
import Skills from './components/Skills';

function App() {
  const [resumeData, setResumeData] = useState({
    personalInfo: {
      name: 'ชื่อ นามสกุล',
      title: 'ตำแหน่งงาน',
      email: 'email@example.com',
      phone: '012-345-6789',
      address: 'ที่อยู่',
      summary: 'สรุปประวัติส่วนตัวและประสบการณ์การทำงาน'
    },
    experience: [
      {
        id: 1,
        company: 'ชื่อบริษัท',
        position: 'ตำแหน่งงาน',
        startDate: '2020-01',
        endDate: '2023-12',
        description: 'รายละเอียดงานและความรับผิดชอบ'
      }
    ],
    education: [
      {
        id: 1,
        institution: 'ชื่อมหาวิทยาลัย/สถาบัน',
        degree: 'ปริญญา/ประกาศนียบัตร',
        field: 'สาขาวิชา',
        graduationYear: '2020'
      }
    ],
    skills: ['JavaScript', 'React', 'Node.js', 'HTML', 'CSS']
  });

  const updatePersonalInfo = (newInfo) => {
    setResumeData(prev => ({
      ...prev,
      personalInfo: { ...prev.personalInfo, ...newInfo }
    }));
  };

  const addExperience = (experience) => {
    const newExperience = {
      ...experience,
      id: Date.now()
    };
    setResumeData(prev => ({
      ...prev,
      experience: [...prev.experience, newExperience]
    }));
  };

  const updateExperience = (id, updatedExperience) => {
    setResumeData(prev => ({
      ...prev,
      experience: prev.experience.map(exp => 
        exp.id === id ? { ...exp, ...updatedExperience } : exp
      )
    }));
  };

  const deleteExperience = (id) => {
    setResumeData(prev => ({
      ...prev,
      experience: prev.experience.filter(exp => exp.id !== id)
    }));
  };

  const addEducation = (education) => {
    const newEducation = {
      ...education,
      id: Date.now()
    };
    setResumeData(prev => ({
      ...prev,
      education: [...prev.education, newEducation]
    }));
  };

  const updateEducation = (id, updatedEducation) => {
    setResumeData(prev => ({
      ...prev,
      education: prev.education.map(edu => 
        edu.id === id ? { ...edu, ...updatedEducation } : edu
      )
    }));
  };

  const deleteEducation = (id) => {
    setResumeData(prev => ({
      ...prev,
      education: prev.education.filter(edu => edu.id !== id)
    }));
  };

  const updateSkills = (skills) => {
    setResumeData(prev => ({
      ...prev,
      skills: skills
    }));
  };

  return (
    <div className="container">
      <div className="resume-container">
        <Header personalInfo={resumeData.personalInfo} />
        <div className="resume-content">
          <PersonalInfo 
            personalInfo={resumeData.personalInfo}
            onUpdate={updatePersonalInfo}
          />
          <Experience 
            experience={resumeData.experience}
            onAdd={addExperience}
            onUpdate={updateExperience}
            onDelete={deleteExperience}
          />
          <Education 
            education={resumeData.education}
            onAdd={addEducation}
            onUpdate={updateEducation}
            onDelete={deleteEducation}
          />
          <Skills 
            skills={resumeData.skills}
            onUpdate={updateSkills}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
