import React, { useState } from 'react';

function Education({ education, onAdd, onUpdate, onDelete }) {
  const [isAdding, setIsAdding] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [formData, setFormData] = useState({
    institution: '',
    degree: '',
    field: '',
    graduationYear: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingId) {
      onUpdate(editingId, formData);
      setEditingId(null);
    } else {
      onAdd(formData);
    }
    setFormData({
      institution: '',
      degree: '',
      field: '',
      graduationYear: ''
    });
    setIsAdding(false);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleEdit = (edu) => {
    setFormData(edu);
    setEditingId(edu.id);
    setIsAdding(true);
  };

  const handleCancel = () => {
    setFormData({
      institution: '',
      degree: '',
      field: '',
      graduationYear: ''
    });
    setIsAdding(false);
    setEditingId(null);
  };

  const handleDelete = (id) => {
    if (window.confirm('คุณต้องการลบข้อมูลการศึกษานี้หรือไม่?')) {
      onDelete(id);
    }
  };

  return (
    <div className="section">
      <h2 className="section-title">การศึกษา</h2>
      
      {education.map(edu => (
        <div key={edu.id} className="info-item">
          <h3>{edu.degree}</h3>
          <p><strong>สถาบัน:</strong> {edu.institution}</p>
          <p><strong>สาขา:</strong> {edu.field}</p>
          <p className="date">จบการศึกษา: {edu.graduationYear}</p>
          
          <div className="edit-buttons">
            <button className="btn" onClick={() => handleEdit(edu)}>
              แก้ไข
            </button>
            <button className="btn btn-danger" onClick={() => handleDelete(edu.id)}>
              ลบ
            </button>
          </div>
        </div>
      ))}
      
      {isAdding && (
        <div className="edit-mode">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>ชื่อสถาบัน:</label>
              <input
                type="text"
                name="institution"
                value={formData.institution}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>ระดับการศึกษา:</label>
              <input
                type="text"
                name="degree"
                value={formData.degree}
                onChange={handleChange}
                placeholder="เช่น ปริญญาตรี, ปริญญาโท"
                required
              />
            </div>
            
            <div className="form-group">
              <label>สาขาวิชา:</label>
              <input
                type="text"
                name="field"
                value={formData.field}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>ปีที่จบการศึกษา:</label>
              <input
                type="number"
                name="graduationYear"
                value={formData.graduationYear}
                onChange={handleChange}
                min="1950"
                max="2030"
                required
              />
            </div>
            
            <div className="edit-buttons">
              <button type="submit" className="btn">
                {editingId ? 'อัปเดต' : 'เพิ่ม'}
              </button>
              <button type="button" className="btn btn-secondary" onClick={handleCancel}>
                ยกเลิก
              </button>
            </div>
          </form>
        </div>
      )}
      
      {!isAdding && (
        <div className="edit-buttons">
          <button className="btn" onClick={() => setIsAdding(true)}>
            เพิ่มข้อมูลการศึกษา
          </button>
        </div>
      )}
    </div>
  );
}

export default Education;
