import React, { useState } from 'react';

function PersonalInfo({ personalInfo, onUpdate }) {
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState(personalInfo);

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdate(formData);
    setIsEditing(false);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleCancel = () => {
    setFormData(personalInfo);
    setIsEditing(false);
  };

  return (
    <div className="section">
      <h2 className="section-title">ข้อมูลส่วนตัว</h2>
      
      {isEditing ? (
        <div className="edit-mode">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>ชื่อ-นามสกุล:</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>ตำแหน่งงาน:</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>อีเมล:</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>เบอร์โทรศัพท์:</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>ที่อยู่:</label>
              <input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label>สรุปประวัติ:</label>
              <textarea
                name="summary"
                value={formData.summary}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="edit-buttons">
              <button type="submit" className="btn">บันทึก</button>
              <button type="button" className="btn btn-secondary" onClick={handleCancel}>
                ยกเลิก
              </button>
            </div>
          </form>
        </div>
      ) : (
        <div className="info-item">
          <h3>{personalInfo.name}</h3>
          <p><strong>ตำแหน่ง:</strong> {personalInfo.title}</p>
          <p><strong>อีเมล:</strong> {personalInfo.email}</p>
          <p><strong>โทรศัพท์:</strong> {personalInfo.phone}</p>
          <p><strong>ที่อยู่:</strong> {personalInfo.address}</p>
          <p><strong>สรุปประวัติ:</strong></p>
          <p>{personalInfo.summary}</p>
          
          <div className="edit-buttons">
            <button className="btn" onClick={() => setIsEditing(true)}>
              แก้ไขข้อมูล
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default PersonalInfo;
