import React, { useState } from 'react';

function Portfolio() {
  const [selectedImage, setSelectedImage] = useState(null);

  const portfolioItems = [
    {
      id: 1,
      title: 'E-commerce Website',
      description: 'เว็บไซต์ขายของออนไลน์ที่พัฒนาด้วย React และ Node.js',
      image: 'https://via.placeholder.com/400x300/667eea/ffffff?text=E-commerce+Website',
      technologies: ['React', 'Node.js', 'MongoDB', 'Stripe API'],
      category: 'Web Development'
    },
    {
      id: 2,
      title: 'Mobile App UI/UX',
      description: 'การออกแบบ UI/UX สำหรับแอปพลิเคชันมือถือ',
      image: 'https://via.placeholder.com/400x300/764ba2/ffffff?text=Mobile+App+UI',
      technologies: ['Figma', 'Adobe XD', 'Sketch'],
      category: 'UI/UX Design'
    },
    {
      id: 3,
      title: 'Dashboard Analytics',
      description: 'Dashboard สำหรับแสดงข้อมูลและ analytics',
      image: 'https://via.placeholder.com/400x300/48bb78/ffffff?text=Analytics+Dashboard',
      technologies: ['Vue.js', 'Chart.js', 'D3.js'],
      category: 'Data Visualization'
    },
    {
      id: 4,
      title: 'Social Media Platform',
      description: 'แพลตฟอร์มโซเชียลมีเดียสำหรับแบ่งปันรูปภาพ',
      image: 'https://via.placeholder.com/400x300/ed8936/ffffff?text=Social+Platform',
      technologies: ['React Native', 'Firebase', 'AWS'],
      category: 'Mobile Development'
    },
    {
      id: 5,
      title: 'Portfolio Website',
      description: 'เว็บไซต์ Portfolio สำหรับแสดงผลงาน',
      image: 'https://via.placeholder.com/400x300/9f7aea/ffffff?text=Portfolio+Website',
      technologies: ['Next.js', 'Tailwind CSS', 'Framer Motion'],
      category: 'Web Development'
    },
    {
      id: 6,
      title: 'Task Management App',
      description: 'แอปพลิเคชันจัดการงานและโปรเจค',
      image: 'https://via.placeholder.com/400x300/38b2ac/ffffff?text=Task+Management',
      technologies: ['Angular', 'TypeScript', 'PostgreSQL'],
      category: 'Web Development'
    }
  ];

  const categories = ['All', 'Web Development', 'UI/UX Design', 'Data Visualization', 'Mobile Development'];
  const [activeCategory, setActiveCategory] = useState('All');

  const filteredItems = activeCategory === 'All' 
    ? portfolioItems 
    : portfolioItems.filter(item => item.category === activeCategory);

  const openModal = (item) => {
    setSelectedImage(item);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  return (
    <div className="section">
      <h2 className="section-title">
        <i className="icon">🎨</i>
        ผลงาน
      </h2>

      {/* Category Filter */}
      <div className="category-filter">
        {categories.map(category => (
          <button
            key={category}
            className={`filter-btn ${activeCategory === category ? 'active' : ''}`}
            onClick={() => setActiveCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Portfolio Grid */}
      <div className="portfolio-grid">
        {filteredItems.map(item => (
          <div key={item.id} className="portfolio-item" onClick={() => openModal(item)}>
            <div className="portfolio-image">
              <img src={item.image} alt={item.title} />
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {selectedImage && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close" onClick={closeModal}>×</button>
            <div className="modal-image">
              <img src={selectedImage.image} alt={selectedImage.title} />
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Portfolio;
