import React, { useState } from 'react';

function Skills({ skills, onUpdate }) {
  const [isEditing, setIsEditing] = useState(false);
  const [skillsText, setSkillsText] = useState(skills.join(', '));

  const handleSubmit = (e) => {
    e.preventDefault();
    const skillsArray = skillsText
      .split(',')
      .map(skill => skill.trim())
      .filter(skill => skill.length > 0);
    onUpdate(skillsArray);
    setIsEditing(false);
  };

  const handleCancel = () => {
    setSkillsText(skills.join(', '));
    setIsEditing(false);
  };

  return (
    <div className="section">
      <h2 className="section-title">ทักษะ</h2>
      
      {isEditing ? (
        <div className="edit-mode">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>ทักษะ (คั่นด้วยจุลภาค):</label>
              <textarea
                value={skillsText}
                onChange={(e) => setSkillsText(e.target.value)}
                placeholder="เช่น JavaScript, React, Node.js, HTML, CSS"
                required
              />
            </div>
            
            <div className="edit-buttons">
              <button type="submit" className="btn">บันทึก</button>
              <button type="button" className="btn btn-secondary" onClick={handleCancel}>
                ยกเลิก
              </button>
            </div>
          </form>
        </div>
      ) : (
        <div>
          <div className="skills-list">
            {skills.map((skill, index) => (
              <span key={index} className="skill-tag">
                {skill}
              </span>
            ))}
          </div>
          
          <div className="edit-buttons">
            <button className="btn" onClick={() => setIsEditing(true)}>
              แก้ไขทักษะ
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Skills;
